function c(e,o,n=12){const t=n*60*60;document.cookie=`${encodeURIComponent(e)}=${encodeURIComponent(o)}; max-age=${t}; path=/`}function i(e){const o=document.cookie.match(new RegExp("(^| )"+e+"=([^;]+)"));return o?decodeURIComponent(o[2]):null}function s(e){document.cookie=`${encodeURIComponent(e)}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/`}function m(){const e=document.cookie.split(";");for(const o of e){const n=o.indexOf("="),t=n>-1?o.substr(0,n).trim():o.trim();document.cookie=`${t}=;expires=Thu, 01 Jan 1970 00:00:00 GMT;path=/`}}const u={setCookie:c,getCookie:i,deleteCookie:s,clearAllCookies:m};export{u as c};
